<template>
  <div
    v-if="!isLoading"
    class="root"
    :style="{
      backgroundImage: `url(${appStore.getBgImg})`,
      backgroundSize: '100% 100%',
    }"
  >
    <div class="logo">
      <img alt="logo" :src="appStore.getLogo" />
      <div class="logo-text">{{ appStore.getTitle }}</div>
    </div>
    <div class="container">
      <div class="left-banner"></div>
      <div class="login-card">
        <div class="title">
          {{ appStore.getTitle + $t('login.admin.welcome') }}
        </div>
        <Forget v-if="isForget" @toggleLogin="toggleLogin" />
        <a-tabs v-else class="account-tab">
          <a-tab-pane
            v-if="sysConfig.admin_login.account_login"
            :title="$t('login.account')"
          >
            <AccountLogin @toggleForget="toggleForget" />
          </a-tab-pane>
          <a-tab-pane
            v-if="sysConfig.admin_login.email_login"
            :title="$t('login.email')"
          >
            <EmailLogin @toggleForget="toggleForget" />
          </a-tab-pane>
        </a-tabs>
      </div>
    </div>
    <div class="footer">
      <Footer />
    </div>
    <a-layout-footer class="beian">
      <a-link
        v-if="appStore.getIcpBeian"
        target="_blank"
        href="https://beian.miit.gov.cn"
      >
        {{ appStore.getIcpBeian }}
      </a-link>
      &nbsp;
      <a-link
        v-if="appStore.getGaBeian"
        target="_blank"
        :href="`https://beian.mps.gov.cn/#/query/webSearch?code=${
          appStore.getGaBeian ? appStore.getGaBeian.match(`\\d+`) : ''
        }`"
      >
        <img
          style="border: 0"
          src="data:image/png;base64,"
        />
        &nbsp;
        {{ appStore.getGaBeian }}
      </a-link>
    </a-layout-footer>
  </div>
</template>

<script lang="ts" setup>
  import { ref, onMounted } from 'vue';
  import { useRoute, useRouter } from 'vue-router';
  import { useUserStore, useAppStore } from '@/store';
  import { querySysConfig, SysConfigDetail } from '@/api/sys_config';
  import Footer from '@/components/footer/index.vue';
  import AccountLogin from './components/admin-account-login.vue';
  import EmailLogin from './components/admin-email-login.vue';
  import Forget from './components/admin-forget.vue';

  useUserStore().logout();

  const appStore = useAppStore();
  const route = useRoute();
  const router = useRouter();

  const isForget = ref(false);
  const isLoading = ref(true);

  const toggleLogin = () => {
    isForget.value = false;
  };

  const toggleForget = () => {
    isForget.value = true;
  };

  const sysConfig = ref<SysConfigDetail>({
    admin_login: {},
  } as SysConfigDetail);

  onMounted(() => {
    querySysConfig({
      domain: window.location.hostname,
      path: window.location.pathname,
    })
      .then((res) => {
        sysConfig.value = res.data;

        const currentPath = route.params.adminPath as string;

        if (
          !sysConfig.value.admin_login.path ||
          currentPath !== sysConfig.value.admin_login.path
        ) {
          router.push({ name: 'notFound' });
          return;
        }

        localStorage.setItem('adminPath', currentPath);
        isLoading.value = false;
      })
      .catch(() => {
        router.push({ name: 'notFound' });
      });
  });
</script>

